import './App.css';
import React, { Component }from 'react';

/*import Card from './components/Card';*/
import Cards from './components/Cards';

class App extends Component {
  state = {
    cardsArr: [
      {name: "card1", color: "red", id: 1},
      {name: "card2", color: "green", id: 2},
      {name: "card3", color: "blue", id: 3},
      {name: "card4", color: "purple", id: 4},
      {name: "card5", color: "red", id: 5},
      {name: "card6", color: "green", id: 6},
      {name: "card7", color: "blue", id: 7},
      {name: "card8", color: "purple", id: 8},
      {name: "card9", color: "red", id: 9},
      {name: "card10", color: "green", id: 10},
      {name: "card11", color: "blue", id: 11},
      {name: "card12", color: "purple", id: 12},
      {name: "card13", color: "red", id: 13},
      {name: "card14", color: "green", id: 14},
      {name: "card15", color: "blue", id: 15},
      {name: "card16", color: "purple", id: 16},
    ],
    twoClicked: false
  };

  flipCardHandler = () => {
    this.setState({twoClicked: true});
  }

  render() {
    /*let cards = this.state.cardsArr.map(card => {
      return <Card name={card.name} color={card.color} click={this.flipCardHandler}/>
    })*/
    const style = {
      backgroundColor: 'black',
      margin: '20px'

    }
    /*if (this.state.twoClicked === false) {
      style
    }*/

    return (
      <div className="App">
        <h1>Shell Game</h1>
        <Cards cardsArr={this.state.cardsArr} click={this.flipCardHandler}/>
        <button style={style}></button>
      </div>
    );
  }

}

export default App;
