import './App.css';
import React, { Component }from 'react';
import WinMessage from './components/WinMessage';
import image from './assets/brown.png';
/*import classes from '*.module.css';*/
import banana from './assets/icons/banana.jpg';
import coconut from './assets/icons/coconut.png';
import palm from './assets/icons/palmtree.png';
import rum from './assets/icons/rum.png';

/*import Card from './components/Card';*/
/*import Cards from './components/Cards';*/

class App extends Component {
  state = {
    cardsArr: [
      {name: "card1", id: 1, isClicked: false, type: "a", removed: false, icon: banana},
      {name: "card2", id: 2, isClicked: false, type: "b", removed: false, icon: coconut},
      {name: "card3", id: 3, isClicked: false, type: "c", removed: false, icon: palm},
      {name: "card4", id: 4, isClicked: false, type: "d", removed: false, icon: rum},
      {name: "card5", id: 5, isClicked: false, type: "e", removed: false},
      {name: "card6", id: 6, isClicked: false, type: "f", removed: false},
      {name: "card7", id: 7, isClicked: false, type: "g", removed: false},
      {name: "card8", id: 8, isClicked: false, type: "h", removed: false},
      {name: "card9", id: 9, isClicked: false, type: "a", removed: false, icon: banana},
      {name: "card10", id: 10, isClicked: false, type: "b", removed: false, icon: coconut},
      {name: "card11", id: 11, isClicked: false, type: "c", removed: false, icon: palm},
      {name: "card12", id: 12, isClicked: false, type: "d", removed: false, icon: rum},
      {name: "card13", id: 13, isClicked: false, type: "e", removed: false},
      {name: "card14", id: 14, isClicked: false, type: "f", removed: false},
      {name: "card15", id: 15, isClicked: false, type: "g", removed: false},
      {name: "card16", id: 16, isClicked: false, type: "h", removed: false},
    ],
    didWin: false
  };

  flipCardHandler = (id) => {
    console.log(this.state.cardsArr);
    const newArray = [...this.state.cardsArr];
    const element = newArray[id - 1];
    element.isClicked = !element.isClicked;
    this.setState({cardsArr: newArray});
    setTimeout(() => {

      console.log(this.state.cardsArr);

      let sc = [];
      newArray.forEach((card, index) => {
        if (newArray[index].isClicked === true) {
            sc.push(newArray[index]);
        };
        return sc;
      });
      console.log(sc);

      if (sc.length === 2) {
        //IF CARDS MATCH
        if (sc[0].type === sc[1].type) {
          /* function x() {
            console.log("match");
          }
          setTimeout(x, 3000);
          setTimeout(this.removeHandler(sc[0].id, sc[1].id), 3000);
          setTimeout(this.checkWinHandler, 3000); */

          this.removeHandler(sc[0].id, sc[1].id);
          this.checkWinHandler();
        } else {
          //IF CARDS DONT MATCH
          console.log("don't match");
          this.setFalseHandler();
        }
      }
    }, 3000);






  }

  removeHandler = (id1, id2) => {
      let nArr = this.state.cardsArr;
      nArr.forEach((el, index) => {
        if (nArr[index].id === id1) {
            nArr[index].removed = true;
            nArr[index].isClicked = false;

        }
        if (nArr[index].id === id2) {
            nArr[index].removed = true;
            nArr[index].isClicked = false;
        }
      });
      this.setState({cardsArr: nArr});
  }

  setFalseHandler = () => {
    const nArr = this.state.cardsArr;
      nArr.map((card, index) => {
        return nArr[index].isClicked = false;
      })
      this.setState({cardsArr: nArr})
  }

  checkWinHandler = () => {
    const nArr = this.state.cardsArr;
    let didWin = 0;
    for (let i = 0; i < this.state.cardsArr.length; i++) {
      if (nArr[i].removed) {
        didWin++;
      }
    }
    if (didWin === 16) {
      this.setState({didWin: true});
    }
  }

  somethingHandler = (id) => {
    console.log("hi");
    /*const arr = this.state.cardsArr;*/

  }

  render() {
    /* const style1 = {
      backgroundColor: "transparent",
      cursor: "none"
    }
    const style2 = {
      backgroundColor: "blue",
    } */

    let cardStyle = {
      position: "relative",
      width: "100%",
      height: "100%",
      textAlign: "center",
      transition: "transform 1s",
      transformStyle: "preserve-3d",
      boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2)",
      borderRadius: "20px",
      opacity: "1"
    };
    let cardStyle2 = {
      display: "none"
    }
    let cardStyle3 = {
      position: "relative",
      width: "100%",
      height: "100%",
      textAlign: "center",
      transition: "transform 1s",
      transformStyle: "preserve-3d",
      boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2)",
      transform: "rotateY(180deg)",
      borderRadius: "20px",
      opacity: "1"
    }


    let winMessage = "";
    if (this.state.didWin) {
      winMessage = <WinMessage />;
    }


    let cards = this.state.cardsArr.map((card) => {
      return <div className="Ccard"
                  name={card.name} 
                  id={card.id} 
                  key={card.id}>
                <div onClick={() => this.flipCardHandler(card.id)} 
                    style={this.state.cardsArr[card.id -1].removed ? cardStyle2
                    : this.state.cardsArr[card.id - 1].isClicked ? cardStyle3
                    : cardStyle}>
                  <div className="card__face card__face--front"><img src={image} alt=""/></div>
                  <div className="card__face card__face--back"><img src={card.icon} alt=""/></div>
                </div>
              </div>
    })

    return (
      <div className="App">
        <h1>Matching Game</h1>
        <div className="Cards">
          {cards}
        </div>
        {winMessage}
      </div>
    );
  }

}

export default App;
