import './App.css';
import React, { Component }from 'react';

/*import Card from './components/Card';*/
/*import Cards from './components/Cards';*/

class App extends Component {
  state = {
    cardsArr: [
      {name: "card1", id: 1, isClicked: false, type: "a"},
      {name: "card2", id: 2, isClicked: false, type: "b"},
      {name: "card3", id: 3, isClicked: false, type: "c"},
      {name: "card4", id: 4, isClicked: false, type: "d"},
      {name: "card5", id: 5, isClicked: false, type: "e"},
      {name: "card6", id: 6, isClicked: false, type: "f"},
      {name: "card7", id: 7, isClicked: false, type: "g"},
      {name: "card8", id: 8, isClicked: false, type: "h"},
      {name: "card9", id: 9, isClicked: false, type: "a"},
      {name: "card10", id: 10, isClicked: false, type: "b"},
      {name: "card11", id: 11, isClicked: false, type: "c"},
      {name: "card12", id: 12, isClicked: false, type: "d"},
      {name: "card13", id: 13, isClicked: false, type: "e"},
      {name: "card14", id: 14, isClicked: false, type: "f"},
      {name: "card15", id: 15, isClicked: false, type: "g"},
      {name: "card16", id: 16, isClicked: false, type: "h"},
    ],
    twoClicked: false
  };

  flipCardHandler = (id) => {
    /* let x = this.state.cardsArr.find((el) => {
      return el.id === id;
    }); */
    const arrZ = [...this.state.cardsArr];
    const elZ = arrZ[id - 1];
    elZ.isClicked = !elZ.isClicked;
    /* console.log(elZ);
    console.log(arrZ); */

    this.setState({cardsArr: arrZ})

    /* console.log(x);
    let newName = "card" + x.id;
    this.setState({cardsArr: [
      {name: newName, id: x.id, isClicked: !this.state.isClicked}
    ]
      }) */
    /* let y = <Card name={x.name} key={x.id}/>;
    console.log(y);
    let z = window.getComputedStyle(y);
    console.log(z); */
  }

  /* removeHandler = (id1, id2) => {
    const nArr = [...this.state.cardsArr];
    const firstId = nArr.find((el) => {
      if (nArr[el].id === id1) {
        return nArr[el].id;
      }
    });
    const secondId = nArr.find((el) => {
      if (nArr[el].id === id2) {
        return nArr[el].id;
      }
    });
    nArr.forEach((el) => {
      if (nArr[el].id === firstId) {
        nArr.splice(firstId, 1);
      }
      if (nArr[el].id === secondId) {
        nArr.splice(secondId, 1);
      }
    });
    console.log(nArr);

  } */

  render() {
    const style = {
      backgroundColor: "blue",
    }


    let cards = this.state.cardsArr.map((card) => {
      /* return <Card name={card.name} color={card.color} click={() => this.flipCardHandler(card.id)} key={card.id} /> */
      return <div className="Ccard" name={card.name} id={card.id} style={this.state.cardsArr[card.id - 1].isClicked ? style : null} onClick={() => this.flipCardHandler(card.id)} key={card.id}></div>
    })

    let sc = [];
    const newArr = [...this.state.cardsArr];
    /*console.log(newArr);*/
    let selectedCards = newArr.forEach((card, index) => {
      /*console.log("this is: " + newArr[index].isClicked);*/
      if (newArr[index].isClicked === true) {
          sc.push(newArr[index]);
      };
      return sc;
    })
    console.log(sc);
    if (sc.length === 2) {
      console.log("win");
      if (sc[0].type === sc[1].type) {
        /*removeHandler(sc[0].id, sc[1].id)*/
        let nArr = [...this.state.cardsArr];
        nArr.forEach((el) => {
          if (nArr[el].id === sc[0].id) {
            nArr.splice(nArr[el].id, 1);
          }
          if (nArr[el].id === sc[1].id) {
            nArr.splice(nArr[el].id, 1);
          }
        });
      } else {
        const nArr = this.state.cardsArr;
        nArr.map((card, index) => {
          return nArr[index].isClicked = false;
        })
        this.setState({cardsArr: nArr})
      }
    }
    /* const style = {
      display: 'flex',
      flexWrap: 'wrap',
      width: '60%',
      backgroundColor: 'thistle',
      margin: 'auto'
    } */

    return (
      <div className="App">
        <h1>Shell Game</h1>
        {/* <Cards cardsArr={this.state.cardsArr} click={(cardName) => {
          return this.flipCardHandler(this.state.cardsArr)}
        }/>
        <button style={style}></button> */}
        <div className="Cards">
          {cards}
        </div>
      </div>
    );
  }

}

export default App;
