import './App.css';
import React, { Component }from 'react';
import WinMessage from './components/WinMessage';
/*import classes from '*.module.css';

/*import Card from './components/Card';*/
/*import Cards from './components/Cards';*/

class App extends Component {
  state = {
    cardsArr: [
      {name: "card1", id: 1, isClicked: false, type: "a", removed: false},
      {name: "card2", id: 2, isClicked: false, type: "b", removed: false},
      {name: "card3", id: 3, isClicked: false, type: "c", removed: false},
      {name: "card4", id: 4, isClicked: false, type: "d", removed: false},
      {name: "card5", id: 5, isClicked: false, type: "e", removed: false},
      {name: "card6", id: 6, isClicked: false, type: "f", removed: false},
      {name: "card7", id: 7, isClicked: false, type: "g", removed: false},
      {name: "card8", id: 8, isClicked: false, type: "h", removed: false},
      {name: "card9", id: 9, isClicked: false, type: "a", removed: false},
      {name: "card10", id: 10, isClicked: false, type: "b", removed: false},
      {name: "card11", id: 11, isClicked: false, type: "c", removed: false},
      {name: "card12", id: 12, isClicked: false, type: "d", removed: false},
      {name: "card13", id: 13, isClicked: false, type: "e", removed: false},
      {name: "card14", id: 14, isClicked: false, type: "f", removed: false},
      {name: "card15", id: 15, isClicked: false, type: "g", removed: false},
      {name: "card16", id: 16, isClicked: false, type: "h", removed: false},
    ],
    didWin: false
  };

  flipCardHandler = (id) => {
    const arrZ = [...this.state.cardsArr];
    const elZ = arrZ[id - 1];
    elZ.isClicked = !elZ.isClicked;
    this.setState({cardsArr: arrZ});

    let sc = [];
    arrZ.forEach((card, index) => {
      if (arrZ[index].isClicked === true) {
          sc.push(arrZ[index]);
      };
      return sc;
    });
    console.log("this is sc: " + sc);
    if (sc.length === 2) {
      //IF CARDS MATCH
      if (sc[0].type === sc[1].type) {
        this.removeHandler(sc[0].id, sc[1].id);
        this.checkWinHandler();
      } else {
        //IF CARDS DONT MATCH
        this.setFalseHandler();
      }
    }


  }

  removeHandler = (id1, id2) => {
      let nArr = this.state.cardsArr;
      nArr.forEach((el, index) => {
        if (nArr[index].id === id1) {
            nArr[index].removed = true;
            nArr[index].isClicked = false;

        }
        if (nArr[index].id === id2) {
            nArr[index].removed = true;
            nArr[index].isClicked = false;
        }
      });
  }

  setFalseHandler = () => {
    const nArr = this.state.cardsArr;
      nArr.map((card, index) => {
        return nArr[index].isClicked = false;
      })
      this.setState({cardsArr: nArr})
  }

  checkWinHandler = () => {
    const nArr = this.state.cardsArr;
    let didWin = 0;
    for (let i = 0; i < this.state.cardsArr.length; i++) {
      if (nArr[i].removed) {
        didWin++;
      }
    }
    if (didWin === 16) {
      this.setState({didWin: true});
    }
  }

  somethingHandler = (id) => {
    console.log("hi");
    /*const arr = this.state.cardsArr;*/

  }

  render() {
    /* const style1 = {
      backgroundColor: "transparent",
      cursor: "none"
    }
    const style2 = {
      backgroundColor: "blue",
    } */

    let cardStyle = {
      position: "relative",
      width: "100%",
      height: "100%",
      textAlign: "center",
      transition: "transform 1s",
      transformStyle: "preserve-3d",
      boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2)"
    };
    let cardStyle2 = {
      position: "relative",
      width: "100%",
      height: "100%",
      textAlign: "center",
      transition: "transform 1s",
      transformStyle: "preserve-3d",
      boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2)",
      backgroundColor: "black"
    }
    let cardStyle3 = {
      position: "relative",
      width: "100%",
      height: "100%",
      textAlign: "center",
      transition: "transform 1s",
      transformStyle: "preserve-3d",
      boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2)",
      transform: "rotateY(180deg)"
    }


    let winMessage = "";
    if (this.state.didWin) {
      winMessage = <WinMessage />;
    }


    let cards = this.state.cardsArr.map((card) => {
      /* return <Card name={card.name} color={card.color} click={() => this.flipCardHandler(card.id)} key={card.id} /> */
      return <div className="Ccard"
                  name={card.name} 
                  id={card.id} 
                 /*  style={
                    this.state.cardsArr[card.id -1].removed ? style1
                    : this.state.cardsArr[card.id - 1].isClicked ? style2 
                    : null}  */ 
                  key={card.id}>
                <div onClick={() => this.flipCardHandler(card.id)} 
                    style={this.state.cardsArr[card.id -1].removed ? cardStyle2
                    : this.state.cardsArr[card.id - 1].isClicked ? cardStyle3
                    : cardStyle}>
                  <div className="card__face card__face--front">front</div>
                  <div className="card__face card__face--back">back</div>
                </div>
              </div>
    })

    return (
      <div className="App">
        <h1>Shell Game</h1>
        {/* <Cards cardsArr={this.state.cardsArr} click={(cardName) => {
          return this.flipCardHandler(this.state.cardsArr)}
        }/>
        <button style={style}></button> */}
        <div className="Cards">
          {cards}
        </div>
        {winMessage}
      </div>
    );
  }

}

export default App;
