import './App.css';
import React, { Component }from 'react';

import Card from './components/Card';

class App extends Component {
  state = {
    cardsArr: [
      {name: "card1", color: "red"},
      {name: "card2", color: "green"},
      {name: "card3", color: "blue"},
      {name: "card4", color: "purple"}
    ]
  };

  render() {
    let cards = this.state.cardsArr.map(card => {
      <Card name={this.state.cardsArr[card].name} color={this.state.cardsArr[card].color}/>
    })

    return (
      <div className="App">
        <h1>Shell Game</h1>
        {cards}
      </div>
    );
  }

}

export default App;

