import Card from './Card';
import './Cards.css';

const Cards = props => {
    let cards = props.cardsArr.map(card => {
        return <Card name={card.name} color={card.color} key={card.id} click={props.click}/>
    })

    return <div className="Cards">
        {cards}
    </div>
}

export default Cards;
